mapper.select:
select id, passwd, name, phone, type 
from Member 
where id = #{ id } and passwd = #{ passwd }

select name, content, type, address, parking, hostNo, hostId, holiday, openStart, openEnd
from Host
where hostId = #{ hostId }

mapper.update:
update Member
set passwd = #{ passwd }, phone = #{ phone }
where id = #{ id }
