mapper.select:
select id, passwd, name, phone, type 
from Member 
where id = #{ id } and passwd = #{ passwd }

mapper.update:
update Member
set passwd = #{ passwd }, phone = #{ phone }
where id = #{ id }

mapper.insert:
INSERT INTO HOST(
	hostId
	, name
	, content
	, type
	, address
	, parking
	, open
) VALUES(
	#{ hostId }
	, #{ name }
	, #{ content }
	, #{ type }
	, #{ address }
	, #{ parking }
	, #{ open }
)
