mapper.insert:

	SELECT SPACE_SEQUENCE.CURRVAL FROM DUAL

INSERT INTO SPACE(
	spaceNo, spaceName, content, maximum, bim, smoking, food, wifi, computer, hostNo, hostId
)
VALUES
(
	SPACE_SEQUENCE.NEXTVAL
	, #{ spaceName }
	, #{ content }
	, #{ maximum }
	, #{ bim }
	, #{ smoking }
	, #{ food }
	, #{ wifi }
	, #{ computer }
	, #{ hostNo }
	, #{ hostId }
)

INSERT INTO SpaceFILE(
	
)
VALUES
(
	SPACEFILE_SEQUENCE.NEXTVAL
	, #{ spaceNo }
	, #{ savedFileName }
	, #{ flag }
)
